changeSet.sql:
COMMENT ON TABLE flowers IS 'Таблица цветов для флористического учета LAVANDA ERP';
COMMENT ON COLUMN flowers.id IS 'Уникальный идентификатор цветка';
COMMENT ON COLUMN flowers.name IS 'Название цветка';
COMMENT ON COLUMN flowers.type IS 'Тип цветка (ROSE, TULIP, LILY и т.д.)';
COMMENT ON COLUMN flowers.color IS 'Цвет цветка (RED, WHITE, PINK и т.д.)';
COMMENT ON COLUMN flowers.description IS 'Описание цветка';
COMMENT ON COLUMN flowers.price_per_piece IS 'Цена за штуку в рублях';
COMMENT ON COLUMN flowers.stock_quantity IS 'Количество на складе (штуки)';
COMMENT ON COLUMN flowers.reserved_quantity IS 'Зарезервированное количество (штуки)';
COMMENT ON COLUMN flowers.supplier IS 'Поставщик';
COMMENT ON COLUMN flowers.supplier_contact IS 'Контактная информация поставщика';
COMMENT ON COLUMN flowers.expiry_date IS 'Дата истечения срока годности';
COMMENT ON COLUMN flowers.seasonal_availability IS 'Сезонная доступность (ALL_YEAR, SPRING, SUMMER и т.д.)';
COMMENT ON COLUMN flowers.storage_conditions IS 'Условия хранения';
COMMENT ON COLUMN flowers.notes IS 'Дополнительные примечания';
COMMENT ON COLUMN flowers.is_active IS 'Активен ли товар';
COMMENT ON COLUMN flowers.created_at IS 'Дата и время создания записи';
COMMENT ON COLUMN flowers.updated_at IS 'Дата и время последнего обновления';

CREATE OR REPLACE FUNCTION update_updated_at_column()
RETURNS TRIGGER AS $$
BEGIN
    NEW.updated_at = CURRENT_TIMESTAMP;
    RETURN NEW;
END;
$$ language 'plpgsql';

CREATE TRIGGER update_flowers_updated_at 
    BEFORE UPDATE ON flowers 
    FOR EACH ROW 
    EXECUTE FUNCTION update_updated_at_column();
